module.exports = function (robot) {
  robot.respond(/insulto/i, function (message) {
    robot.http('https://insultos.unlimited.pizza/raw').get()(function (err, res, body) {
      if (err) {
        return message.send('No mereces un insulto');
      }

      message.send(body);
    });
  });
};
